"use client";

import { useState } from "react";
import { movieRows } from "./data/movie-data";
import { Movie } from "./types/movie-types";
import { Navbar } from "./components/navbar/Navbar";
import { HeroBanner } from "./components/hero-banner/HeroBanner";
import { MovieRow } from "./components/movie-row/MovieRow";
import { MovieModal } from "./components/movie-modal/MovieModal";
import { Footer } from "./components/footer/Footer";

export default function HomePage() {
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedMovie, setSelectedMovie] = useState<Movie | null>(null);

  const heroMovie = movieRows[0].movies[0];

  const handleMovieClick = (movie: Movie) => {
    setSelectedMovie(movie);
    setModalOpen(true);
  };

  const handleMoreInfo = () => {
    setSelectedMovie(heroMovie);
    setModalOpen(true);
  };

  return (
    <div className="min-h-screen bg-[#0a0a0a] text-white selection:bg-red-600 selection:text-white">
      <div className="relative">
        <Navbar />
        <HeroBanner
          movie={heroMovie}
          onPlay={() => {}}
          onMoreInfo={handleMoreInfo}
        />
      </div>
      <main className="flex-1 w-full max-w-[1920px] mx-auto px-4 md:px-12 mt-[-120px] z-20 relative pb-20">
        <div className="pt-20 space-y-12">
          {movieRows.map((row) => (
            <MovieRow key={row.id} row={row} onMovieClick={handleMovieClick} />
          ))}
        </div>
      </main>
      <Footer />
      <MovieModal open={modalOpen} onOpenChange={setModalOpen} movie={selectedMovie} />
    </div>
  );
}

