import { useState, useEffect } from "react";
import { Search, Bell, User } from "lucide-react";

export function Navbar() {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <nav className={`w-full flex items-center justify-between px-4 md:px-12 py-5 z-50 fixed top-0 transition-all duration-500 ${
      scrolled ? "bg-[#0a0a0a] shadow-lg backdrop-blur-md" : "bg-gradient-to-b from-black via-black/60 to-transparent"
