import { MovieRow as MovieRowType, Movie } from "../../types/movie-types";
import { MovieCard } from "../movie-card/MovieCard";
import { useRef, useState } from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";

function cn(...classes: (string | boolean | undefined | null)[]) {
  return classes.filter(Boolean).join(' ');
}

interface MovieRowProps {
  row: MovieRowType;
  onMovieClick: (movie: Movie) => void;
}

export function MovieRow({ row, onMovieClick }: MovieRowProps) {
  const scrollRef = useRef<HTMLDivElement>(null);
  const [showLeftArrow, setShowLeftArrow] = useState(false);
  const [showRightArrow, setShowRightArrow] = useState(true);

  const scroll = (dir: "left" | "right") => {
    if (!scrollRef.current) return;
    const { scrollLeft, clientWidth } = scrollRef.current;
    const scrollAmount = clientWidth * 0.8;
    scrollRef.current.scrollTo({
      left: dir === "left" ? scrollLeft - scrollAmount : scrollLeft + scrollAmount,
      behavior: "smooth",
    });
  };

  const handleScroll = () => {
    if (scrollRef.current) {
      const { scrollLeft, scrollWidth, clientWidth } = scrollRef.current;
      setShowLeftArrow(scrollLeft > 0);
      setShowRightArrow(Math.ceil(scrollLeft + clientWidth) < scrollWidth);
    }
  };

  return (
    <section className="mb-10 pl-4 md:pl-12 group/row relative">
      <h2 className="text-xl md:text-2xl font-bold text-white mb-4 hover:text-zinc-300 transition-colors cursor-pointer inline-flex items-center gap-3">
        {row.title}
        <span className="text-xs opacity-0 group-hover/row:opacity-100 transition-all bg-zinc-700/80 backdrop-blur-sm px-2 py-1 rounded-full text-white font-normal hidden md:inline-block">Explore All</span>
      </h2>

      <div className="relative group/slider">
        <button
          className={cn(
            "absolute left-0 top-0 bottom-0 z-30 w-14 bg-gradient-to-r from-black/80 via-black/60 to-transparent hover:from-black/90 flex items-center justify-center opacity-0 transition-all duration-300 -translate-x-4 group-hover/slider:translate-x-0 group-hover/slider:opacity-100",
            !showLeftArrow && "hidden"
          )}
          onClick={() => scroll("left")}
        >
          <ChevronLeft size={44} className="text-white drop-shadow-lg" />
        </button>

        <button
          className={cn(
            "absolute right-0 top-0 bottom-0 z-30 w-14 bg-gradient-to-l from-black/80 via-black/60 to-transparent hover:from-black/90 flex items-center justify-center opacity-0 transition-all duration-300 translate-x-4 group-hover/slider:translate-x-0 group-hover/slider:opacity-100",
            !showRightArrow && "hidden"
          )}
          onClick={() => scroll("right")}
        >
          <ChevronRight size={44} className="text-white drop-shadow-lg" />
        </button>

        <div
          ref={scrollRef}
          className="flex gap-3 overflow-x-auto scrollbar-hide scroll-smooth py-6 px-1"
          onScroll={handleScroll}
        >
          {row.movies.map((movie) => (
            <div
              key={movie.id}
              className="flex-none w-[170px] md:w-[260px] aspect-video rounded-lg overflow-hidden transition-all duration-300 hover:scale-110 hover:z-40 cursor-pointer shadow-lg hover:shadow-2xl"
              onClick={() => onMovieClick(movie)}
            >
              <MovieCard movie={movie} />
            </div>
          ))}
        </div>
      </div>
      <style jsx>{`
        .scrollbar-hide::-webkit-scrollbar {
            display: none;
        }
        .scrollbar-hide {
            -ms-overflow-style: none;
            scrollbar-width: none;
        }
      `}</style>
    </section>
  );
}

