import { Movie } from "../../types/movie-types";
import { Play, Info, Plus, ThumbsUp } from "lucide-react";
import { useState } from "react";

function cn(...classes: (string | boolean | undefined | null)[]) {
  return classes.filter(Boolean).join(' ');
}

interface MovieCardProps {
  movie: Movie;
}

export function MovieCard({ movie }: MovieCardProps) {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className="relative w-full h-full bg-zinc-900 rounded-lg transition-all duration-300 group/card"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <img
        src={movie.image}
        alt={movie.title}
        className="w-full h-full object-cover rounded-lg"
      />

      <div className={cn(
        "absolute inset-0 z-10 bg-[#141414] rounded-lg shadow-2xl flex flex-col justify-between overflow-hidden transition-all duration-300 origin-center border border-zinc-700/50",
        isHovered ? "opacity-100 scale-125 delay-150" : "opacity-0 scale-100 pointer-events-none"
      )}>
        <div className="relative">
          <img
            src={movie.image}
            alt={movie.title}
            className="w-full h-32 object-cover"
          />
          <div className="absolute inset-0 bg-gradient-to-t from-[#141414] to-transparent" />
        </div>

        <div className="p-4 flex-1 flex flex-col justify-between">
          <div>
            <div className="flex gap-2 mb-3">
              <button className="bg-white text-black rounded-full p-2 hover:bg-opacity-90 transition-all shadow-md hover:scale-105">
                <Play size={16} fill="black" className="ml-0.5" />
              </button>
              <button className="border-2 border-zinc-500 rounded-full p-2 text-white hover:border-white transition-all hover:scale-105">
                <Plus size={16} />
              </button>
              <button className="border-2 border-zinc-500 rounded-full p-2 text-white hover:border-white transition-all hover:scale-105">
                <ThumbsUp size={16} />
              </button>
              <button className="ml-auto border-2 border-zinc-500 rounded-full p-2 text-white hover:border-white transition-all hover:scale-105">
                <Info size={16} />
              </button>
            </div>

            <div className="flex items-center gap-2 text-xs font-bold text-green-400 mb-2">
              <span>{movie.match}% Match</span>
              <span className="border border-zinc-600 px-1.5 py-0.5 text-[10px] text-zinc-400 rounded">HD</span>
              <span className="text-zinc-400 font-normal">{movie.year}</span>
            </div>

            <div className="flex flex-wrap gap-1.5 mb-2">
               {movie.genres.slice(0, 3).map((g, i) => (
                 <span key={g} className="text-[11px] text-zinc-400">
                   {g}{i < movie.genres.slice(0, 3).length - 1 && <span className="mx-1">•</span>}
                 </span>
               ))}
            </div>
          </div>
        </div>
      </div>

      {!isHovered && (
        <div className="absolute inset-0 bg-gradient-to-t from-black/40 to-transparent group-hover/card:from-black/60 transition-all duration-300 rounded-lg" />
      )}
    </div>
  );
}

