interface MovieModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  movie: Movie | null;
}

export function MovieModal({ open, onOpenChange, movie }: MovieModalProps) {
  const [liked, setLiked] = useState<boolean | null>(null);
  const [added, setAdded] = useState(false);

  if (!open || !movie) return null;

  return (
    <div
      className="fixed inset-0 z-50 flex items-start justify-center pt-8 px-4 bg-black/90 backdrop-blur-md overflow-y-auto animate-in fade-in duration-300"
      onClick={() => onOpenChange(false)}
