        onClick={(e) => e.stopPropagation()}
        style={{ boxShadow: '0 0 60px rgba(0,0,0,0.8), 0 0 120px rgba(229,9,20,0.15)' }}
      >
        {/* Close Button - Enhanced */}
        <button
          className="absolute top-5 right-5 z-50 bg-[#181818]/95 backdrop-blur-sm rounded-full p-2.5 text-white hover:bg-zinc-700 transition-all shadow-xl border border-zinc-700/50 hover:scale-110 active:scale-95"
