        {/* Hero Section */}
        <div className="relative">
          <img
            src={movie.image}
            alt={movie.title}
            className="w-full h-[45vh] md:h-[55vh] object-cover"
          />
          {/* Enhanced Gradients */}
          <div className="absolute inset-0 bg-gradient-to-t from-[#181818] via-[#181818]/50 to-transparent" />
